import React, { useState, useEffect } from 'react';
import { useWallet, useConnection } from '@solana/wallet-adapter-react';
import { LAMPORTS_PER_SOL, PublicKey } from '@solana/web3.js';
import { StatsCard } from './StatsCard';

const WalletTrackerCard = () => {
  const { publicKey, connected } = useWallet();
  const { connection } = useConnection();
  const [balance, setBalance] = useState(0);
  const [tokenCount, setTokenCount] = useState(0);

  useEffect(() => {
    const fetchWalletData = async () => {
      if (connected && publicKey) {
        try {
          // Fetch SOL balance
          const bal = await connection.getBalance(publicKey);
          setBalance(bal / LAMPORTS_PER_SOL);

          // Fetch token accounts
          const tokenAccounts = await connection.getParsedTokenAccountsByOwner(publicKey, {
            programId: new PublicKey('TokenkegQfeZyiNwAJbNbGKPFXCWuBvf9Ss623VQ5DA'),
          });
          setTokenCount(tokenAccounts.value.length);
        } catch (error) {
          console.error('Error fetching wallet data:', error);
        }
      }
    };

    fetchWalletData();
    // Set up polling interval
    const interval = setInterval(fetchWalletData, 30000);
    return () => clearInterval(interval);
  }, [connected, publicKey, connection]);

if (!connected) {
return (
    <div className="relative backdrop-blur-lg bg-white/30 rounded-xl p-6 transition-all duration-300 hover:shadow-xl border border-white/20">
    <div className="absolute inset-0 bg-gradient-to-br from-emerald-500/10 to-teal-500/10 rounded-xl"></div>
    <h2 className="relative text-xl font-bold mb-4 bg-gradient-to-r from-emerald-600 to-teal-600 bg-clip-text text-transparent">
        Wallet Tracker
    </h2>
    <div className="relative animate-pulse">
        <p className="text-gray-600">Connect your wallet to view balance</p>
    </div>
    </div>
);
}

return (
<div className="relative backdrop-blur-lg bg-white/30 rounded-xl p-6 transition-all duration-300 hover:shadow-xl border border-white/20">
    <div className="absolute inset-0 bg-gradient-to-br from-emerald-500/10 to-teal-500/10 rounded-xl"></div>
    <div className="relative">
    <h2 className="text-xl font-bold mb-6 bg-gradient-to-r from-emerald-600 to-teal-600 bg-clip-text text-transparent flex items-center gap-2">
        Wallet Tracker
        <div className="w-2 h-2 rounded-full bg-emerald-500 animate-ping"></div>
    </h2>

    <div className="space-y-6">
        <StatsCard
        label="Total Balance"
        value={`${balance.toFixed(4)} SOL`}
        change={"+0.00"}
        />

        <StatsCard
        label="Number of Tokens"
        value={tokenCount.toString()}
        isCount={true}
        />

        <div className="mt-4">
        <div className="h-40 w-full bg-gradient-to-b from-emerald-100/20 to-transparent rounded-lg flex items-center justify-center">
            <p className="text-gray-400 text-sm">Chart Coming Soon</p>
        </div>
        </div>

        <div className="text-sm text-gray-500 truncate hover:text-emerald-600 transition-colors duration-200">
        {publicKey?.toBase58()}
        </div>
    </div>
    </div>
</div>
);
};

export default WalletTrackerCard;
