import React, { useEffect, useState } from 'react';
import WalletTrackerCard from '../components/WalletTrackerCard';
import VolumeTrackerCard from '../components/VolumeTrackerCard';
import WatchedCoinsCard from '../components/WatchedCoinsCard';
import CardWrapper from '../components/CardWrapper';

function Home() {
const [isVisible, setIsVisible] = useState(false);
const [stats, setStats] = useState({ portfolio: 0, growth: 0, coins: 0 });

useEffect(() => {
    setIsVisible(false);

    // Simulate API call
    setTimeout(() => {
        setStats({
            portfolio: 124500,
            growth: 23.5,
            coins: 12
        });
        setIsVisible(true);
    }, 1000);
}, []);

return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 dark:from-gray-900 dark:to-gray-800">
    <div className={`transition-all duration-1000 transform ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'}`}>
        {/* Hero Section */}
        <div className="px-6 py-16 backdrop-blur-sm">
        <div className="max-w-7xl mx-auto">
            <h1 className="text-6xl font-bold mb-6 bg-gradient-to-r from-emerald-500 to-teal-600 bg-clip-text text-transparent animate-gradient">
            Welcome to CryptoTracker
            </h1>
            <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mb-12 leading-relaxed">
            Your personal dashboard for tracking crypto assets, market changes, and the latest trends in real-time.
            </p>

            {/* Stats Section */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16">
            <div className="stats-card">
                <span className="text-sm text-gray-500 dark:text-gray-400">Portfolio Value</span>
                <div className="text-3xl font-bold text-emerald-600 dark:text-emerald-400">
                ${stats.portfolio.toLocaleString()}
                </div>
            </div>
            <div className="stats-card">
                <span className="text-sm text-gray-500 dark:text-gray-400">Growth</span>
                <div className="text-3xl font-bold text-emerald-600 dark:text-emerald-400">
                +{stats.growth}%
                </div>
            </div>
            <div className="stats-card">
                <span className="text-sm text-gray-500 dark:text-gray-400">Tracked Coins</span>
                <div className="text-3xl font-bold text-emerald-600 dark:text-emerald-400">
                {stats.coins}
                </div>
            </div>
            </div>
        </div>
        </div>

        {/* Cards Grid */}
        <div className="px-6 pb-16">
        <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8">
            <div className="transform hover:scale-105 transition-transform duration-300">
            <CardWrapper 
                title="Wallet Tracker" 
                subtitle="Monitor your crypto portfolio"
                className="bg-white/30 dark:bg-gray-800/30 backdrop-blur-lg shadow-xl hover:shadow-2xl transition-all duration-300"
            >
                <WalletTrackerCard />
            </CardWrapper>
            </div>
            <div className="transform hover:scale-105 transition-transform duration-300">
            <CardWrapper 
                title="Watched Coins" 
                subtitle="Your monitored cryptocurrencies"
                className="bg-white/30 dark:bg-gray-800/30 backdrop-blur-lg shadow-xl hover:shadow-2xl transition-all duration-300"
            >
                <WatchedCoinsCard />
            </CardWrapper>
            </div>
            <div className="transform hover:scale-105 transition-transform duration-300">
            <CardWrapper 
                title="Volume Tracker" 
                subtitle="Track trading volume changes"
                className="bg-white/30 dark:bg-gray-800/30 backdrop-blur-lg shadow-xl hover:shadow-2xl transition-all duration-300"
            >
                <VolumeTrackerCard />
            </CardWrapper>
            </div>
        </div>
        </div>
    </div>
    </div>
);
}

export default Home;
