import { NavLink } from 'react-router-dom';
import { 
    HomeIcon,
    ChartBarIcon, 
    WalletIcon 
} from '@heroicons/react/24/outline';

const Navbar = () => {
const navItems = [
    {
        title: 'Home',
        path: '/',
        icon: HomeIcon,
    },
    {
        title: 'Dashboard',
        path: '/dashboard',
        icon: ChartBarIcon,
    },
    {
        title: 'Wallet',
        path: '/wallet-tracker',
        icon: WalletIcon,
    },
];

return (
    <nav className="h-full w-[68px] md:w-[220px] bg-zinc-900 dark:bg-zinc-900 border-r border-zinc-200 dark:border-zinc-800 shadow-lg flex flex-col py-6 transition-all duration-300 ease-in-out">
        <div className="mb-8 px-4">
            <div className="h-10 w-10 md:h-12 md:w-12 bg-emerald-500 hover:bg-emerald-400 transition-colors duration-200 rounded-xl flex items-center justify-center">
                <span className="text-white font-bold text-xl">C</span>
            </div>
        </div>

    <div className="flex flex-col space-y-1.5 px-3">
        {navItems.map((item) => (
        <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) =>
            `flex items-center px-3.5 h-11 rounded-lg transition-all duration-200 ease-in-out relative
            ${isActive 
                ? 'bg-emerald-500/10 text-emerald-500 dark:text-emerald-400 font-medium before:absolute before:left-0 before:w-1 before:h-6 before:bg-emerald-500 before:rounded-r before:top-1/2 before:-translate-y-1/2' 
                : 'text-zinc-600 dark:text-zinc-400 hover:bg-zinc-100 dark:hover:bg-zinc-800/50 hover:text-zinc-900 dark:hover:text-white'
            }`
            }
        >
            <item.icon className="h-5 w-5 flex-shrink-0" title={item.title} />
            <span className="hidden md:block ml-3 text-sm">
            {item.title}
            </span>
        </NavLink>
        ))}
    </div>
    </nav>
);
};

export default Navbar;
