import { NavLink } from 'react-router-dom';
import { useWallet } from '@solana/wallet-adapter-react';
import { WalletMultiButton } from '@solana/wallet-adapter-react-ui';
import { 
    HomeIcon,
    ChartBarIcon, 
    WalletIcon,
} from '@heroicons/react/24/outline';
const Navbar = () => {
    const { connected } = useWallet();

    const navItems = [
        {
            title: 'Home',
            path: '/',
            icon: HomeIcon,
        },
        {
            title: 'Dashboard',
            path: '/dashboard',
            icon: ChartBarIcon,
        },
        {
            title: 'Wallet',
            path: '/wallet-tracker',
            icon: WalletIcon,
        },
    ];

    return (
        <nav className="min-h-[400px] h-[95vh] w-[60px] sm:w-[70px] bg-white/5 backdrop-blur-md flex flex-col py-2 sm:py-4 transition-all duration-300 ease-in-out rounded-2xl my-2 sm:my-4 mx-2 sm:mx-3 shadow-lg relative hover:bg-white/10">
    <div className="flex items-center justify-between mb-4 sm:mb-6 px-2 sm:px-3">
        <div className="h-12 w-12 bg-gradient-to-br from-emerald-400 to-teal-500 hover:from-emerald-300 hover:to-teal-400 transition-all duration-300 rounded-xl flex items-center justify-center shadow-lg hover:shadow-emerald-500/25 hover:scale-105 cursor-pointer">
            <span className="text-white font-bold text-lg sm:text-xl">C</span>
        </div>
    </div>

    <div className="flex flex-col items-center space-y-2">
        {navItems.map((item) => (
        <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) =>
            `group flex items-center px-3 h-10 rounded-lg transition-all duration-200 ease-in-out relative hover:scale-[1.02]
            ${isActive 
                ? 'bg-gradient-to-r from-emerald-400/10 to-teal-400/10 text-emerald-500 font-medium before:absolute before:left-0 before:w-0.5 before:h-5 before:bg-gradient-to-b before:from-emerald-400 before:to-teal-400 before:rounded-r before:top-1/2 before:-translate-y-1/2' 
                : 'text-gray-600 hover:bg-white/5 hover:text-emerald-400'
            }`
            }
        >
            <item.icon className="h-5 w-5 transition-all duration-300 group-hover:scale-110" title={item.title} />
        </NavLink>
        ))}
    </div>

    <WalletMultiButton 
        className="mt-auto mb-4 mx-auto p-2 rounded-lg transition-all duration-200 hover:scale-105
            bg-gradient-to-r from-emerald-400/10 to-teal-400/10 hover:from-emerald-400/20 hover:to-teal-400/20
            !bg-transparent !border-0 !h-auto group relative"
    />
</nav>
);
};

export default Navbar;
