// Mock data for development
const MOCK_TOKENS = {
  'BONK': {
    price: 0.00001234,
    volume24h: 15000000,
    supply: 500000000000,
    priceChange24h: 5.67
  },
  'SAMO': {
    price: 0.0145,
    volume24h: 8000000,
    supply: 100000000,
    priceChange24h: -2.34
  },
  'COPE': {
    price: 0.089,
    volume24h: 3000000,
    supply: 50000000,
    priceChange24h: 1.23
  }
};

// Simulate network delay
const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

export const fetchTokenData = async (tokenSymbol) => {
  try {
    // Simulate network delay
    await delay(500);

    const tokenData = MOCK_TOKENS[tokenSymbol] || {
      price: Math.random() * 0.1,
      volume24h: Math.random() * 10000000,
      supply: Math.random() * 1000000000,
      priceChange24h: (Math.random() - 0.5) * 20
    };

    return tokenData;
  } catch (error) {
    console.error('Error fetching token data:', error);
    throw new Error('Failed to fetch token data. Please try again later.');
  }
};

export const getWalletBalance = async (walletAddress) => {
  try {
    // Simulate network delay
    await delay(300);

    // Return mock balance
    return Math.random() * 100; // Random balance between 0 and 100 SOL
  } catch (error) {
    console.error('Error fetching wallet balance:', error);
    throw new Error('Failed to fetch wallet balance. Please try again later.');
  }
};

export const getTokenAccounts = async (walletAddress) => {
  try {
    // Simulate network delay
    await delay(400);

    // Return mock token accounts
    return Object.keys(MOCK_TOKENS).map(symbol => ({
      account: {
        data: {
          parsed: {
            info: {
              mint: `mock-mint-${symbol}`,
              tokenAmount: {
                amount: String(Math.floor(Math.random() * 1000000)),
                decimals: 9,
                uiAmount: Math.random() * 1000
              }
            }
          }
        }
      }
    }));
  } catch (error) {
    console.error('Error fetching token accounts:', error);
    throw new Error('Failed to fetch token accounts. Please try again later.');
  }
};
